import React, { useState } from 'react';
import axios from 'axios';
import { API_BASE_URL } from '../apiConfig';
import './RefundPage.css';

function RefundPage({ bookingId }) {
  const [reason, setReason] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleRefund = async (e) => {
    e.preventDefault();
    setMessage('');
    setIsError(false);
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${API_BASE_URL}/api/refunds`,
        { bookingId, reason },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log('Refund response:', res);

      if (res.status === 201) {
        setMessage('✅ Refund request submitted successfully.');
        setIsError(false);
        setReason('');
      } else {
        setMessage('❌ Unexpected response from server.');
        setIsError(true);
      }
    } catch (err) {
      console.error('Refund error:', err.response || err);
      const msg = err.response?.data?.error || '❌ Failed to submit refund request.';
      setMessage(msg);
      setIsError(true);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="refund-page">
      <h2>Request a Refund</h2>
      <form onSubmit={handleRefund} className="refund-form">
        <label>Reason for refund (optional):</label>
        <textarea
          value={reason}
          onChange={(e) => setReason(e.target.value)}
          placeholder="Write your reason here..."
          rows={4}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Submitting...' : 'Submit Refund Request'}
        </button>
      </form>
      {message && (
        <div className={`refund-message ${isError ? 'error' : 'success'}`}>
          {message}
        </div>
      )}
    </div>
  );
}

export default RefundPage;
