// src/pages/AdminPanel.js
import './AdminPanel.css';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import DashboardChart from '../components/DashboardChart';

const AdminPanel = () => {
  const [settings, setSettings] = useState({ allowCancellation: true, cancellationHoursLimit: 24 });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchSettings = async () => {
      try {
        const res = await axios.get('/api/settings/booking');
        setSettings(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching settings:', err);
        setLoading(false);
      }
    };
    fetchSettings();
  }, []);

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;
    setSettings(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : Number(value)
    }));
  };

  const handleSave = async () => {
    try {
      await axios.put('/api/settings/booking', settings);
      setMessage('Settings saved successfully ‚úÖ');
    } catch (err) {
      setMessage('Failed to save settings ‚ùå');
    }
  };

  if (loading) return <div className="admin-panel">Loading admin settings...</div>;

  return (
    <div className="admin-panel-container">
      <div className="admin-sidebar">
        <h2>üëë Skybridge Admin Panel</h2>
        <button className="admin-btn">
          <Link to="/admin" className="admin-link">üìä Dashboard</Link>
        </button>
        <button className="admin-btn">
          <Link to="/admin/booking-settings" className="admin-link">‚öôÔ∏è Booking Settings</Link>
        </button>
        <button className="admin-btn">
          <Link to="/admin/manage-staff" className="admin-link">üë• Manage Staff</Link>
        </button>
        <button className="admin-btn">
          <Link to="/admin/view-visas" className="admin-link">üìÑ Visa Applications</Link>
        </button>
        <button className="admin-btn">
          <Link to="/admin/view-bookings" className="admin-link">üìë All Bookings</Link>
        </button>
        <button className="admin-btn">
          <Link to="/admin/audit-logs" className="admin-link">üïµÔ∏è Audit Logs</Link>
        </button>
        <button className="admin-btn">
          <Link to="/admin/manage-flights" className="admin-link">‚úàÔ∏è Manage Flights</Link>
        </button>
      </div>

      <div className="admin-content">
        <div className="admin-welcome">
          <h3>üìå Welcome, Admin</h3>
          <p>Use the navigation on the left to manage system settings, staff, bookings, and visa applications.</p>
        </div>

        {/* ‚úÖ Dashboard Pie Chart Summary */}
        <DashboardChart />

        <hr />
        <div className="admin-settings">
          <h4>üîß Booking Cancellation Settings</h4>
          <div className="form-check form-switch mb-3">
            <input
              className="form-check-input"
              type="checkbox"
              id="allowCancellation"
              name="allowCancellation"
              checked={settings.allowCancellation}
              onChange={handleChange}
            />
            <label className="form-check-label" htmlFor="allowCancellation">
              Allow Cancellation
            </label>
          </div>

          <div className="mb-3">
            <label htmlFor="cancellationHoursLimit" className="form-label">
              Hours before flight allowed for cancellation:
            </label>
            <input
              type="number"
              className="form-control"
              id="cancellationHoursLimit"
              name="cancellationHoursLimit"
              value={settings.cancellationHoursLimit}
              min="1"
              onChange={handleChange}
            />
          </div>

          <button className="btn btn-success" onClick={handleSave}>üíæ Save Settings</button>
          {message && <p className="mt-3">{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
